// Micro $ so other stubs don't explode. This is NOT full jQuery.
(function () {
  function $(sel) {
    const els = typeof sel === "string" ? document.querySelectorAll(sel) : [sel];
    const api = {
      elements: Array.from(els),
      on(ev, fn) { this.elements.forEach(el => el.addEventListener(ev, fn)); return this; },
      addClass(c) { this.elements.forEach(el => el.classList.add(c)); return this; },
      removeClass(c) { this.elements.forEach(el => el.classList.remove(c)); return this; }
    };
    return api;
  }
  $.fn = {};
  window.$ = window.jQuery = $;
})();